var login = (function () {
  return {
    authorize: function (id, callback) {
      $.post('/get-user', id, function (data) {
        $.each(preparedObj.offers, function () {
          this.user = data;
        });
        login.renderUser();
        if (callback) {
          callback();
        }
      }, 'json', 'text/plain');
    },
    renderUser: function () {
      $('.login-wrap .author').html(preparedObj.userTempl(preparedObj.offers[0].user));
    }
  }
})();